// lib/form-validator.js
class FormValid {
  constructor(form) {
    this.form = form;
    this.errors = {};
  }

  isEmpty(field) {
    if (!this.form[field] || this.form[field].trim() === "") {
      this.errors[field] = `${field} cannot be empty`;
    }
  }

  isEmail(field) {
    const re = /\S+@\S+\.\S+/;
    if (!re.test(this.form[field])) {
      this.errors[field] = `${field} is not a valid email address`;
    }
  }

  getErrors() {
    return this.errors;
  }
}

module.exports = FormValid;
